    public void charu(){
        int[] a = {49, 38, 65, 97, 76, 13, 27, 49, 78, 34, 12, 64, 1};
        System.out.println("");
        System.out.println(Arrays.toString(a));

        //
        for(int i=0;i<a.length;i++){
            for (int j=i;j>0;j--){
                if(a[j]>a[j-1]){
                    break;
                }
                int x=a[j];
                a[j]=a[j-1];
                a[j-1]=x;
            }

        }
        System.out.println(Arrays.toString(a));
    }
