 public void xier(){
        int[] a = {49, 38, 65, 97, 76, 13, 27, 49, 78, 34, 12, 64, 1};
        System.out.println("");
        System.out.println(Arrays.toString(a));
        //
        for (int group = a.length / 2; group > 0; group /= 2) {
            for (int i = group; i < a.length; i++) {
                for (int j = i; j > group - 1; j -= group) {
                    if (a[j] > a[j - group]) {
                        break;
                    }
                    int x = a[j];
                    a[j] = a[j - group];
                    a[j - group] = x;
                }
            }
        }
        System.out.println(Arrays.toString(a));
    }
